declare module "schoologyapi" {
	export default class SchoologyAPI {
		oauth: any
		constructor(key: string, secret: string)
		request(method: string, path: string, body?: object, accessToken?: string): Promise<any>
		createRequestToken: () => Promise<any>
		getAccessToken: (requestToken: string) => Promise<any>
		createOAuthURL: (requestToken: string, district: string, callback: string) => string
		formatToken: (token: string) => { key: string, secret: string }
	}
}